mapper.select:
/* mapmapper.selectMap */
SELECT id, x, y, z, road_address, gibun_address
FROM map




				AND x = #{roadAddress.x}


				AND y = #{roadAddress.y}




				AND x = #{gibunAddress.x}


				AND y = #{gibunAddress.y}

mapper.update:
/* mapmapper.updateMap */
UPDATE map SET 

	min_x = #{minX}


	max_x = #{maxX}


	min_y = #{minY}


	max_y = #{maxY}
